import React, { Component } from 'react';
import {UncontrolledCarousel} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

const items = [
    {
        src : 'data:image/jpeg;base64,',
        altText : '슬라이드 1 이미지 대체 문구',
        caption : '슬라이드 1 설명',
        header : '슬라이드 1 제목'
    },
    {
        src : 'data:image/jpeg;base64,',
        altText : '슬라이드 2 이미지 대체 문구',
        caption: ' 슬라이드 2 설명',
        header : '슬라이드 2 제목'
    },
    {   
        src : 'data:image/jpeg;base64,',
        altText : '슬라이드 3 이미지 대체 문구',
        caption: ' 슬라이드 3 설명',
        header : '슬라이드 3 제목'
    }
];

class ImageSlideShow extends Component {
    constructor(props) {
        super(props);
        this.state = {
          activeIndex: 0,
        };
      }

      componentDidMount() {
        this.interval = setInterval(() => {
          this.setState((prevState) => ({
            activeIndex: (prevState.activeIndex + 1) % items.length,
          }));
        }, 3000);
      }
    
      componentWillUnmount() {
        clearInterval(this.interval);
      }
    render() {
        const { activeIndex } = this.state;
        return (
          <div style={{
            width: '1200px',
            height: '450px',
            padding: '20px',
            position: 'absolute',
            left: '50%',
            top: '250%',
            transform: 'translate(-50%, -50%)',
          }}>
                <UncontrolledCarousel activeIndex={activeIndex} items = {items} style={{maxWidth: '700px', margin: '0 auto'}}/>
                <br></br>
                <img src={items[0].src}></img>
                <img src={items[1].src}></img>
                <img src={items[2].src}></img>
            </div>
        );
    }
}

export default ImageSlideShow;